import React from 'react';
import { ethers } from 'ethers';
import axios from 'axios';        // we use axios to make http requests to our backend 

const API_URL = 'http://localhost:4000';        ////we define a variable to point to our backend. but if we deploy on heroku or fleek the URL will be different

const ITEMS = [                           //we also define a catalog of ITEMS
  {
    id: 1,
    price: ethers.utils.parseEther('100')
  }, 
  {
    id: 2,
    price: ethers.utils.parseEther('200')
  }
];
//we use the destructured props from the component in App.js in Store({})
//we return our HTML/JSX as a list using the css bootstrap class list group
function Store({ paymentProcessor, dai }) {

  return (
    <ul className="list-group">
      <li className="list-group-item">
        Buy item1 - <span className='badge'>100 DAI</span>

      </li>
    </ul>
  );
}

export default Store;
